package Basics;

public class Expressions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int friends = 10; 
		friends = friends + 1;
		friends = friends - 1;
		friends = friends * 1;
		friends = friends / 2;
		friends = friends % 2;
		friends++;
		System.out.println(friends);
	}

}
